mcl_damage.register_modifier(function(obj, damage, reason)     if obj:is_player() then         local pname = obj:get_player_name()         if (pname == "Bear123" or pname == "Robin213") then             if reason.type == "out_of_world" then                 local ppos = obj:get_pos(); local _, is_in_deadly_void = mcl_worlds.is_in_void(ppos); if not is_in_deadly_void then return end                 local hp = obj:get_hp();                 if hp + damage <= 20 then                     return hp + damage;                 else                     return 20                 end             end         end     end end, 1012) 