local img_list = {
    lemon_lime = "",
    donut_chocolate = "",
}

local fbox = { type = "fixed", fixed = { -8 / 16, -1 / 2, -8 / 16, 8 / 16, -7.5 / 16, 8 / 16 } }

for k, v in pairs(img_list) do
    local texture = "blank.png^[png:" .. v
    minetest.register_node( ":arkacia:images_" .. k, {
        description = "Image: " .. k,
        drawtype = "nodebox",
        tiles = {
            { name = texture },
            { name = texture },
            { name = "blank.png" },
            { name = "blank.png" },
            { name = "blank.png" },
            { name = "blank.png" },
        },
        use_texture_alpha = "clip",
        node_box = fbox,
        collision_box = fbox,
        groups = { oddly_breakable_by_hand = 1, z_item = 1, dig_immediate = 3 },
        selection_box = fbox,

        paramtype = "light",
        paramtype2 = "wallmounted",
        sunlight_propagates = true,
        node_placement_prediction = "",

        inventory_image = texture,
    })
end
