local textures = {
    brown_autumnal = "",
    fir = "",
    orange_autumnal = "",
    red_autumnal = "",
    yellow_autumnal = "",
    azalea = "",
    flowering_azalea = "",
    spruce = "",
}
for k, v in pairs(textures) do
    minetest.register_node(":arkacia:opaque_leaves_" .. k, {
        description = "Opaque Leaves: " .. k,
        tiles = { "blank.png^[png:" .. v },
        groups = { dig_immediate = 3, }
    })
end




minetest.register_node(":arkacia:leaves_spruce_opaque", {
    description = "Opaque Spruce Leaves",
    waving = 2,
    paramtype2 = "color",
    palette = "mcl_core_palette_leaves.png",
    groups = {
        handy = 1,
        hoey = 1,
        shearsy = 1,
        swordy = 1,
        deco_block = 1,
        biomecolor = 1,
        compostability = 30,
        unsticky = 1,
        dig_immediate = 3,
    },
    sounds = mcl_sounds.node_sound_leaves_defaults(),
    tiles = { "blank.png^[png:" .. textures.spruce },
})
